import React, { useEffect, useMemo, useState } from 'react';

function Dashboard() {
  const [auditsReport, setAuditsReport] = useState([]);
  const [ingestedData, setIngestedData] = useState([]);
  const [remediationData, setRemediationData] = useState([]);
  const [message, setMessage] = useState('');
  const [report, setReport] = useState(null);

  const handleIngestEmbeddings = async () => {
    setMessage('');
    setReport(null); // Hide report when ingesting embeddings
    try {
      // API call for embedding
      const response = await fetch('http://localhost:5001/api/v1/knowledge/ingest', { method: 'POST', body: JSON.stringify({}), headers: { 'Content-Type': 'application/json' } });
      if (response.ok) {
        setMessage('All Security Policies Ingested successfully!');
        const data = await response.json();
        setIngestedData([data]);
      } else {
        setMessage('Failed to ingest Security Policies.');
      }
    } catch (error) {
      console.error('Error ingesting Security Policies:', error);
      setMessage('Error ingesting Security Policies.');
    }
  };

  const handleStartAuditor = async () => {
    setMessage('');
    setReport(null);
    setAuditsReport([]);
    setIngestedData([]); // Clear ingested data when starting auditor
    // The report will be set to data.report if the API call is successful, making it visible.
    try {
      // API call for auditor
      const response = await fetch('http://localhost:5000/api/v1/ai-agents/audit', { method: 'POST', body: JSON.stringify({}), headers: { 'Content-Type': 'application/json' } });
      console.log('Response from auditor API:', response.body);
      if (response.ok) {
        const data = await response.json();
        // Extract only the service audit report list and normalize to array of objects
        if (data && data.audit_report) {
          setAuditsReport(JSON.stringify({
            "audit_report": data.audit_report
          }));
          const reportArr = Object.entries(data.audit_report).map(([key, value]) => {
            // Normalize policy_checks for table rendering
            let policy_checks = [];
            if (Array.isArray(value.policies)) {
              policy_checks = value.policies.map(p => ({
                ...p,
                comply: typeof p.comply === 'string' ? p.comply.toLowerCase() === 'comply' : !!p.comply,
                missing_plugins: p.missing_plugins || [],
                policy_name: p.policy_name,
                required_for: p.required_for || p.policy_name,
                details: p.details || ''
              }));
            } else if (Array.isArray(value.results)) {
              policy_checks = value.results.map(p => ({
                ...p,
                comply: typeof p.comply === 'string' ? p.comply.toLowerCase() === 'comply' : !!p.comply,
                missing_plugins: p.missing_plugins || [],
                policy_name: p.policy_name,
                required_for: p.required_for || p.policy_name,
                details: p.details || ''
              }));
            }
            return {
              serviceName: value.serviceName,
              policy_checks
            };
          });
          setReport(reportArr);
        }
        setMessage('Audit Completed successfully!');
      } else {
        setMessage('Failed to start auditor.');
      }
    } catch (error) {
      console.error('Error starting auditor:', error);
      setMessage('Error starting auditor.');
    }
  };

  const handleRemediation = async () => {
    setMessage('');
    //setReport(null);
    setIngestedData([]);

    try {
      const response = await fetch('http://localhost:5000/api/v1/ai-agents/remediate', {
        method: 'POST',
        body: {
          "audit_report": {
            "ApigeeToKongGenAIService:policy:ApigeeToKongGenAIService": {
              "policies": [
                {
                  "comply": false,
                  "details": "A mandatory authentication plugin (key-auth, oauth2, or jwt) is required to enforce access controls.",
                  "missing_plugins": [
                    "key-auth",
                    "oauth2",
                    "jwt"
                  ],
                  "policy_name": "Authentication Policy"
                },
                {
                  "comply": false,
                  "details": "A rate-limiting plugin is required to protect against abuse and DoS attacks.",
                  "missing_plugins": [
                    "rate-limiting"
                  ],
                  "policy_name": "Rate Limiting Policy"
                },
                {
                  "comply": false,
                  "details": "The request-size-limiting plugin is required to prevent oversized payloads.",
                  "missing_plugins": [
                    "request-size-limiting"
                  ],
                  "policy_name": "Request Size Limiting Policy"
                },
                {
                  "comply": false,
                  "details": "At least one logging plugin (http-log, file-log, tcp-log) is required for audit and traceability.",
                  "missing_plugins": [
                    "http-log",
                    "file-log",
                    "tcp-log"
                  ],
                  "policy_name": "Logging Policy"
                }
              ],
              "serviceName": "ApigeeToKongGenAIService"
            },
            "HealthService:policy:HealthService": {
              "policies": [
                {
                  "comply": true,
                  "details": "",
                  "missing_plugins": [],
                  "policy_name": "API Authentication Enforcement"
                },
                {
                  "comply": true,
                  "details": "",
                  "missing_plugins": [],
                  "policy_name": "Payload Security and Manipulation Controls"
                }
              ],
              "serviceName": "HealthService"
            },
            "KongTest:policy:KongTest": {
              "policies": [
                {
                  "comply_status": false,
                  "details": "Multiple authentication plugins are present (oauth2, key-auth, basic-auth), so the policy is met.",
                  "missing_plugins": [],
                  "policy_name": "Authentication Required"
                },
                {
                  "comply_status": true,
                  "details": "rate-limiting plugin is present (multiple times), so the policy is met.",
                  "missing_plugins": [],
                  "policy_name": "Rate Limiting Required"
                },
                {
                  "comply_status": true,
                  "details": "ip-restriction plugin is present, so the policy is met.",
                  "missing_plugins": [],
                  "policy_name": "IP Restriction Required"
                },
                {
                  "comply_status": true,
                  "details": "request-transformer plugin is present, so the policy is met.",
                  "missing_plugins": [],
                  "policy_name": "Request Transformation Required"
                }
              ],
              "serviceName": "KongTest"
            }
          }
        },
        headers: { 'Content-Type': 'application/json' }
      });

      if (response.ok) {
        const data = await response.json();
          alert('Remediation Response: ' + JSON.stringify(data));
          setRemediationData(JSON.stringify(data)); // Assuming you have: const [remediation, setRemediation] = useState([]);
          alert('Remediation Data: ' + JSON.stringify(remediationData));
          setMessage('Remediation Completed successfully!');
        }

    } catch (error) {
      console.error('Error starting Remediator:', error);
      setMessage('Error starting Remediator.');
    }
  };

  const handleGenerateAuditReport = () => {
    alert('Generating Audit report...');
    // Logic to generate Audit report
  };
  // Sort the report so that non-compliant policies appear first
  const sortedReport = useMemo(() => {
    if (!report) return null;
    // Flatten all policy checks with service name for easier table rendering and sorting
    const flat = [];
    report.forEach((serviceObj) => {
      serviceObj.policy_checks.forEach((policy) => {
        flat.push({
          ...policy,
          serviceName: serviceObj.serviceName,
        });
      });
    });
    // Sort: non-compliant first, then compliant
    flat.sort((a, b) => {
      if (a.comply === b.comply) return 0;
      return a.comply ? 1 : -1;
    });
    return flat;
  }, [report]);

  const handleReset = () => {
    setAuditsReport([]);
    setMessage('');
    setReport(null);
    setIngestedData([]);
  };


  return (
    <div className="container mx-auto p-4">
      <div className="flex space-x-4 mb-4">
        <button
          onClick={handleIngestEmbeddings}
          className="bg-orange-500 hover:bg-orange-700 text-white font-bold py-2 px-4 rounded"
        >
          Ingest Security Policies
        </button>
        <button
          onClick={handleStartAuditor}
          className="bg-blue-900 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
        >
          Start Security Audit and Generate Report
        </button>
        <button
          onClick={handleReset}
          className="bg-gray-500 hover:bg-gray-700 text-white font-bold py-2 px-4 rounded"
        >
          Reset
        </button>
      </div>

      {message && (
        <div className={`mb-4 p-3 rounded ${message.includes('successfully') ? 'bg-green-200 text-green-800' : 'bg-red-200 text-red-800'}`}>
          {message}
        </div>
      )}

      {ingestedData.length > 0 && (
        <div className="mb-8">
          <h3 className="text-xl mb-3">Security Policy Information</h3>
          <div className="overflow-x-auto">
            <table className="min-w-full bg-white border border-gray-200">
              <thead>
                <tr>
                  <th className="py-2 px-4 border-b">Index Name</th>
                  <th className="py-2 px-4 border-b">Chunk ID</th>
                  <th className="py-2 px-4 border-b">Content</th>
                  <th className="py-2 px-4 border-b">Metadata</th>
                </tr>
              </thead>
              <tbody>
                {ingestedData.map((item, idx) =>
                  Array.isArray(item.chunks)
                    ? item.chunks.map((chunk, cidx) => (
                      <tr key={`${idx}-${cidx}`}>
                        <td className="py-2 px-4 border-b">{item.index_name}</td>
                        <td className="py-2 px-4 border-b">{chunk.chunk_id}</td>
                        <td className="py-2 px-4 border-b">
                          <pre className="whitespace-pre-wrap text-xs bg-gray-100 p-2 rounded">{chunk.content}</pre>
                        </td>
                        <td className="py-2 px-4 border-b">
                          <pre className="whitespace-pre-wrap text-xs bg-gray-100 p-2 rounded">{chunk.metadata}</pre>
                        </td>
                      </tr>
                    ))
                    : null
                )}
              </tbody>
            </table>
          </div>
        </div>
      )}

      {sortedReport && (
        <div className="mb-8">
          <h3 className="text-xl mb-3">Audit Report</h3>
          <div className="overflow-x-auto">
            <table className="min-w-full bg-white border border-gray-200">
              <thead>
                <tr>
                  <th className="py-2 px-4 border-b">Sr. No.</th>
                  <th className="py-2 px-4 border-b">Service Name</th>
                  <th className="py-2 px-4 border-b">Policy Name</th>
                  <th className="py-2 px-4 border-b">Compliance Status</th>
                  <th className="py-2 px-4 border-b">Missing Plugins</th>
                  <th className="py-2 px-4 border-b">Details</th>
                </tr>
              </thead>
              <tbody>
                {sortedReport.map((policy, idx) => (
                  <tr key={idx} className={policy.comply ? 'bg-green-100' : 'bg-red-100'}>
                    <td className="py-2 px-4 border-b">{idx + 1}</td>
                    <td className="py-2 px-4 border-b">{policy.serviceName}</td>
                    <td className="py-2 px-4 border-b">{policy.policy_name}</td>
                    <td className="py-2 px-4 border-b">{policy.comply ? 'Compliant' : 'Non-Compliant'}</td>
                    <td className="py-2 px-4 border-b">
                      {!policy.comply && policy.missing_plugins && policy.missing_plugins.length > 0
                        ? policy.missing_plugins.map((mp, mpIdx) => (
                          <div key={mpIdx}>
                            <span className="font-semibold">{mp.plugin || mp.name || JSON.stringify(mp)}</span>
                            {mp.required_for && (
                              <span> (Required for: {mp.required_for})</span>
                            )}
                          </div>
                        ))
                        : '-'}
                    </td>
                    <td className="py-2 px-4 border-b">
                      {policy.details ? (
                        <pre className="whitespace-pre-wrap text-xs bg-gray-100 p-2 rounded">{policy.details}</pre>
                      ) : (
                        '-'
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          <div className="flex space-x-4 mt-4">
            <button
              onClick={handleGenerateAuditReport}
              className="bg-blue-900 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
            >
              Download Audit Report
            </button>
            <button
              onClick={handleRemediation}
              className="bg-blue-900 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
            >
              Generate Remediation Plan
            </button>

          </div>
        </div>
      )}

      {remediationData.length > 0 && (
        <div className="mt-8 p-6 bg-white rounded-lg shadow-md">
          <h2 className="text-2xl font-semibold text-gray-800 mb-4">Remediation Plan</h2>
          <table className="min-w-full table-auto border border-gray-300">
            <thead className="bg-gray-100">
              <tr>
                <th className="px-4 py-2 text-left text-sm font-medium text-gray-700">Service Name</th>
                <th className="px-4 py-2 text-left text-sm font-medium text-gray-700">Policy File</th>
                <th className="px-4 py-2 text-left text-sm font-medium text-gray-700">Compliance</th>
                <th className="px-4 py-2 text-left text-sm font-medium text-gray-700">Summary</th>
              </tr>
            </thead>
            <tbody>
              {remediationData.map((item, index) => (
                <tr key={index} className="border-t border-gray-200">
                  <td className="px-4 py-2 text-sm text-gray-800">{item.parsed?.service_name || 'N/A'}</td>
                  <td className="px-4 py-2 text-sm text-gray-800">{item.parsed?.policy_file || 'N/A'}</td>
                  <td className="px-4 py-2 text-sm text-gray-800">
                    {item.parsed?.compliance
                      ? Object.entries(item.parsed.compliance).map(([plugin, status]) => (
                        <div key={plugin}>
                          <span className="font-semibold">{plugin}:</span> {status}
                        </div>
                      ))
                      : 'N/A'}
                  </td>
                  <td className="px-4 py-2 text-sm text-gray-800">{item.parsed?.summary || 'N/A'}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {!report && ingestedData.length === 0 && (
        <div className="mt-8 p-6 bg-gray-100 rounded-lg shadow-md">
          <h2 className="text-2xl font-semibold text-gray-800 mb-4">Welcome to the Autonomous Security Auditor Agent</h2>
          <p className="text-gray-700 mb-4">
            The <b>Autonomous Security Auditor Agentic AI</b> is an advanced application designed to automate the auditing and remediation of API configurations for organizations using Kong API Gateway and Kong AI Gateway. Leveraging AI, large language models (LLMs), and agentic workflows, this tool enables organizations to continuously validate API configurations against enterprise security and compliance policies, identify risks, and remediate issues with minimal manual intervention.
          </p>
          <p className="text-gray-700">
            The Autonomous Security Auditor Agentic AI bridges these gaps by automatically auditing API proxy configurations in Kong against organizational security policies. It generates actionable audit reports and enables one-click remediation, ensuring every API adheres to mandatory security standards. This tool empowers <b>developers, DevSecOps engineers, reviewers, auditors, and architects</b> to maintain compliance and security without deep expertise in every policy, reducing risk and streamlining the review process.
          </p>
        </div>
      )}
    </div>
  );
}

export default Dashboard;
