import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from './AuthContext';
import { login } from './authService';
import Header from './Header';
import Footer from './Footer';
import '../css/CommonStyle.css'
import { Alert, AlertDescription, AlertTitle } from './ui/Alert';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from './ui/Table';
import { Download, AlertCircle, CheckCircle, AlignLeft, AlignJustify } from 'lucide-react';

const Login: React.FC = () => {

    //const [isAuthenticated, setIsAuthenticated] = useState(false);

    const [userId, setUserId] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState<string | null>(null);
    const { login: authLogin } = useAuth();
    const navigate = useNavigate();

    const [loading, setLoading] = useState(false);

    const handleSubmit = async (e: React.FormEvent) => {
        setLoading(true);
        setError(null);
        e.preventDefault();        
        try {

            if (userId === null || userId.trim() === '' || password === null || password.trim() === '') {
                throw new Error("Login failed! Invalid userid or password.");
            }
            const token = await login(userId, password);
            //alert(token);
            if (token == "Invalid credentials") {
               // alert("invalid credentials!");
                throw new Error("Login failed! Invalid userid or password.");
            }
            authLogin(token);
            //setIsAuthenticated(true);
            navigate('/home');
        } catch (err: any) {
            setError(err.message);
        } finally {
           setLoading(false);
        }
    };

    return (
        <div className="flex flex-col h-screen">
            <Header isAuthenticated={false} />
            <div className="flex justify-center items-end h-16 gap-4" style={{ color: 'darkblue' }}>
                <h1 className="text-xl font-bold" >CognizantÂ® API Proxy Migrator for Kong (AKA Apigee to Kong Maker)</h1>
            </div>
            <div className='flex justify-center gap-4' style={{ color: 'darkblue' }}>
                <h3>This automation tool migrates Apigee proxies to their equivalent Kong proxies, including the most commonly used Apigee policies.</h3>
            </div>
            <div className="flex justify-center items-end h-16 gap-4" style={{ color: 'darkblue' }}>
                <h1 className="text-xl font-bold" >Login for Developer</h1>
            </div>
            <div className="flex justify-center rounded h-40 items-center">
                <form onSubmit={handleSubmit}>
                    <input
                        type="text"
                        value={userId}
                        onChange={(e) => setUserId(e.target.value)}
                        placeholder="User id"
                        style={{ padding: '20px', border: '2px solid #90cdf4'}}
                        required                        
                    /> &nbsp;&nbsp;
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        placeholder="Password"
                        style={{ padding: '20px', border: '2px solid #90cdf4'}}
                        required
                    />&nbsp;&nbsp;
                    <button type="submit"
                    className="gap-1 bg-blue-500 hover:bg-blue-700 text-white font-bold rounded"                    
                    >{loading ? 'Loading...' : 'Login'}
                    </button>
                </form>
            </div>
            {error && (
                <Alert variant="destructive" className="flex space-x-2 justify-center items-center mb-6" >
                    <AlertCircle className="h-4 w-4" />
                    <AlertTitle>Error:</AlertTitle>
                    <AlertDescription>{error}</AlertDescription>
                </Alert>

            )}
            <Footer />
        </div>
    );
};

export default Login;
