import React, { useState, useCallback, useRef } from 'react';
import { Alert, AlertDescription, AlertTitle } from './ui/Alert';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from './ui/Table';
import { Download, AlertCircle, CheckCircle, AlignLeft, AlignJustify } from 'lucide-react';
import { motion } from 'framer-motion';
import { text } from 'stream/consumers';
import { inherits } from 'util';

// environment variables
const AWS_GW_HOSTNAME = process.env.REACT_APP_AWS_GW_HOSTNAME as string;
const AWS_API_KEY = process.env.REACT_APP_AWS_API_KEY as string;
const SERVICE_BASEPATH = process.env.REACT_APP_SERVICE_BASEPATH as string;
const KONG_PROXIES_RELEASE_PATHSUFFIX = process.env.REACT_APP_KONG_PROXIES_RELEASE_PATHSUFFIX as string;

type ButtonKey = 'button1Key' | 'button2Key';

interface DeployToKongProps {
    onClose: () => void;
}

interface DeployedProxy {
    kongProxyFileName: string;
    deploymentOutput: string;
    deploymentStatus: string;
}

interface ValidProxy {
    kongProxyFileName: string;
    validationOutput: string;
    validationStatus: string;
}

interface KongDeploymentSummary {
    deploymentSummary: DeployedProxy[];
    validationSummary: ValidProxy[]
}


const DeployToKong: React.FC<DeployToKongProps> = ({ onClose }) => {
    const [kongAdminUrl, setKongAdminUrl] = useState('');
    const [kongAdminToken, setKongAdminToken] = useState('');    
    const [kongProxyDeploymentSummary, setKongProxyDeploymentSummary] = useState<KongDeploymentSummary | null>(null);
    const [error, setError] = useState<string | null>(null);


    const [loading, setLoading] = useState({
        button1Key: false,
        button2Key: false
    });
    const onChangeAdminUrl = (event: React.ChangeEvent<HTMLInputElement>) => {
        setKongAdminUrl(event.target.value);
    };

    const onChangeAdminToken = (event: React.ChangeEvent<HTMLInputElement>) => {
        setKongAdminToken(event.target.value);
    };

    //    const handleDeploy = useCallback(async (buttonKey: ButtonKey) => {
    const handleDeploy = async (buttonKey: ButtonKey) => {
        setLoading((prevState) => ({ ...prevState, [buttonKey]: true }));

        try {

            if (!kongAdminUrl || kongAdminUrl.length === 0) {
                throw new Error("Enter correct Kong Admin URL. e.g. https://mycompanyname.com:8444/");
            } else
                if (!kongAdminToken || kongAdminToken.length === 0) {
                    throw new Error("Enter correct Kong Admin Token. e.g. MyKongAdminTokenOrPassword");
                }
            const response = await fetch(AWS_GW_HOSTNAME + SERVICE_BASEPATH + KONG_PROXIES_RELEASE_PATHSUFFIX, {
                method: 'POST',
                headers: {
                    'kong-admin-url': kongAdminUrl,
                    'kong-admin-token': kongAdminToken,
                    'x-api-key': AWS_API_KEY,
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ "status": "success" }),
            });
            if (response.ok) {
                const jsonResponse = await response.json();
                setKongProxyDeploymentSummary(jsonResponse);
                setError(null);
                
            } else {
                const errorBody = await response.json();
                setError(JSON.stringify(errorBody.error));
                setKongProxyDeploymentSummary(null);
                //setResponseMessage(JSON.stringify(errorBody.error));
            }
        } catch (err: any) {
            setError(err.message);
        } finally {
            setLoading((prevState) => ({ ...prevState, [buttonKey]: false }));
        }
    };

    const handleReset = () => {
        setKongAdminUrl('');
        setKongAdminToken('');
        setKongProxyDeploymentSummary(null);
        setError(null);
    };
    return (
        <div className='flex flex-col h-screen justify-center p-5'>
            <div className="flex justify-center items-center h-16 gap-4" style={{ color: 'darkblue' }}>
                <h1 className="text-xl font-bold" >Kong Proxy Deployment Form</h1>
            </div>
            <div className="row items-center h-screen justify-center" style={{ height: '16rem' }} >
                <div className="h-48 column text-blue-900 rounded-lg"
                    style={{ padding: '20px', border: '2px solid #90cdf4', display: 'flex', justifyContent: 'center', alignItems: 'normal' }} >
                    <label>
                        Kong Admin URL
                        <button className="info-button w-3" onClick={() => alert('Enter the Kong Admin URL.')}>
                            &nbsp;<AlertCircle className="h-4 w-4" /></button>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                        <input
                            //className="border border-blue-300 rounded-lg h-9"
                            style={{ width: '32rem' }}
                            type="text"
                            // name='kongAdminUrl'
                            // id='kongAdminUrl'                       
                            //onChange={onChangeAdminUrl}
                            value={kongAdminUrl}
                            //value="https://ec2-13-60-26-134.eu-north-1.compute.amazonaws.com:8444/"
                            onChange={(e) => {
                                setKongAdminUrl(e.target.value);
                                // console.log(e.target.value);
                            }}
                            placeholder="https://mycompany.com:8444/"
                        />

                    </label>
                    <label>
                        Kong Admin Token
                        <button className="info-button w-3" onClick={() => alert('Enter the authorization token for Kong Admin.')}>
                            &nbsp;<AlertCircle className="h-4 w-4" /></button>&nbsp;&nbsp;&nbsp;&nbsp;
                        <input
                            type="password"
                            style={{ width: '32rem' }}
                            //name='kongAdminToken'
                            //id='kongAdminToken'                       
                            //onChange={onChangeAdminToken}
                            value={kongAdminToken}
                            //value="MyCtsKong"
                            onChange={(e) => {
                                //console.log(e.target.value);
                                setKongAdminToken(e.target.value);
                            }
                            }
                            placeholder="kong-admin-token"
                        />
                    </label>
                </div>
                <div className="flex column gap-1 items-center">
                    <button
                        className={`text-white font-bold py-1 px-4 rounded bg-blue-500 hover:bg-blue-700`}
                        //onClick={handleDeploy}
                        onClick={() => handleDeploy('button1Key')}>
                        {loading.button1Key ? 'loading...' : 'Validate and Deploy'}
                    </button>
                    <button
                        className={`text-white font-bold py-1 px-4 rounded bg-blue-500 hover:bg-blue-700`}
                        onClick={handleReset}>Reset</button>
                    <button
                        className={`text-white font-bold py-1 px-4 rounded bg-blue-500 hover:bg-blue-700`}
                        onClick={onClose}>Close</button>
                </div>
            </div>
            {kongProxyDeploymentSummary && (
                <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.3 }}
                    className="py-4 overflow-y-auto"
                    style={{ justifyItems: 'center', alignItems: 'normal', minHeight: '16rem' }}                    
                >
                    <h2 className="text-xl font-semibold mb-4 text-blue-900 text-center" style={{paddingTop: 'inherits'}}>
                        Kong Proxy Validation Summary</h2>
                    <Table>
                        <TableHeader className="bg-gray-800 text-white">
                            <TableRow>
                                <TableHead>Kong Proxy File Name</TableHead>
                                <TableHead>Proxy Validation Output</TableHead>
                                <TableHead>Status</TableHead>
                            </TableRow>
                        </TableHeader>
                        <TableBody className="text-gray-700">   
                                {kongProxyDeploymentSummary?.validationSummary.map((proxy, index) => (
                                    <TableRow key={`${index}`} className={index % 2 === 0 ? 'bg-gray-100' : 'bg-white'}>
                                        <TableCell>{proxy.kongProxyFileName}</TableCell>
                                        <TableCell dangerouslySetInnerHTML={{ __html: proxy.validationOutput }}></TableCell>
                                        <TableCell>{proxy.validationStatus}</TableCell>
                                    </TableRow>                            
                            ))}
                        </TableBody>
                    </Table>

                    <h2 className="text-xl font-semibold mb-4 text-blue-900 text-center padding:inherit" style={{paddingTop: 'inherits'}}>
                        Kong Proxy Deployment Summary</h2>
                    <Table>
                        <TableHeader className="bg-gray-800 text-white">
                            <TableRow>
                                <TableHead>Kong Proxy File Name</TableHead>
                                <TableHead>Proxy Deployment Output</TableHead>
                                <TableHead>Status</TableHead>
                            </TableRow>
                        </TableHeader>
                        <TableBody className="text-gray-700">   
                                {kongProxyDeploymentSummary?.deploymentSummary.map((proxy, index) => (
                                    <TableRow key={`${index}`} className={index % 2 === 0 ? 'bg-gray-100' : 'bg-white'}>
                                        <TableCell>{proxy.kongProxyFileName}</TableCell>
                                        <TableCell dangerouslySetInnerHTML={{ __html: proxy.deploymentOutput }}></TableCell>
                                        <TableCell>{proxy.deploymentStatus}</TableCell>
                                    </TableRow>                            
                            ))}
                        </TableBody>
                    </Table>
                </motion.div>
            )}
            {error && (
                <Alert variant="destructive" className="flex space-x-2 justify-center items-center text-blue-900" >
                    <AlertCircle className="h-4 w-4" />
                    <AlertTitle>Error:</AlertTitle>
                    <AlertDescription>{error}</AlertDescription>
                </Alert>
            )}
        </div>
    );
};
export default DeployToKong;
