import React, { useEffect, useMemo, useState } from 'react';

function Dashboard() {
  const [auditsReport, setAuditsReport] = useState([]);
  const [ingestedData, setIngestedData] = useState([]);
  const [message, setMessage] = useState('');
  const [report, setReport] = useState(null);

  const [remediationData, setRemediationData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleIngestEmbeddings = async () => {
    setLoading(true);
    setError(null);
    setMessage('');
    setReport(null); // Hide report when ingesting embeddings
    try {
      // API call for embedding
      const response = await fetch('http://localhost:5001/api/v1/knowledge/ingest', { method: 'POST', body: JSON.stringify({}), headers: { 'Content-Type': 'application/json' } });
      if (response.ok) {
        setMessage('All Security Policies Ingested successfully!');
        const data = await response.json();
        setIngestedData([data]);
        setLoading(false);
        setError(null);
      } else {
        setMessage('Failed to ingest Security Policies.');
      }
    } catch (error) {
      console.error('Error ingesting Security Policies:', error);
      setMessage('Error ingesting Security Policies.');
    }
  };

  const handleStartAuditor = async () => {
    setLoading(true);
    setError(null);
    setMessage('');
    setReport(null);
    setAuditsReport([]);
    setIngestedData([]); // Clear ingested data when starting auditor
    // The report will be set to data.report if the API call is successful, making it visible.
    try {
      // API call for auditor
      const response = await fetch('http://localhost:5000/api/v1/ai-agents/audit', { method: 'POST', body: JSON.stringify({}), headers: { 'Content-Type': 'application/json' } });
      console.log('Response from auditor API:', response.body);
      if (response.ok) {
        const data = await response.json();
        // Extract only the service audit report list and normalize to array of objects
        if (data && data.audit_report) {
          setAuditsReport(JSON.stringify({
            "audit_report": data.audit_report
          }));
          const reportArr = Object.entries(data.audit_report).map(([key, value]) => {
            // Normalize policy_checks for table rendering
            let policy_checks = [];
            if (Array.isArray(value.policies)) {
              policy_checks = value.policies.map(p => ({
                ...p,
                comply: typeof p.comply === 'string' ? p.comply.toLowerCase() === 'comply' : !!p.comply,
                missing_plugins: p.missing_plugins || [],
                policy_name: p.policy_name,
                required_for: p.required_for || p.policy_name,
                details: p.details || ''
              }));
            } else if (Array.isArray(value.results)) {
              policy_checks = value.results.map(p => ({
                ...p,
                comply: typeof p.comply === 'string' ? p.comply.toLowerCase() === 'comply' : !!p.comply,
                missing_plugins: p.missing_plugins || [],
                policy_name: p.policy_name,
                required_for: p.required_for || p.policy_name,
                details: p.details || ''
              }));
            }
            return {
              serviceName: value.serviceName,
              policy_checks
            };
          });
          setReport(reportArr);
        }
        setMessage('Audit Completed successfully!');
        setLoading(false);
      } else {
        setMessage('Failed to start auditor.');
      }
    } catch (error) {
      console.error('Error starting auditor:', error);
      setMessage('Error starting auditor.');
    }
  };


  const handleGenerateAuditReport = () => {
    alert('Generating Audit report...');
    // Logic to generate Audit report
  };

  const fetchRemediationPlan = async () => {
    setMessage('');
    setIngestedData([]);
    setLoading(true);
    setError(null);
    //alert('Generating Remediation Plan...' + JSON.stringify(auditsReport));
    try {
      const response = await fetch('http://localhost:5000/api/v1/ai-agents/remediate', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          audit_report: JSON.parse(auditsReport).audit_report
        })
      });

      if (!response.ok) {
        throw new Error(`Server responded with ${response.status}`);
      }

      const data = await response.json();
      alert('In UI ' + JSON.stringify(data));
      setRemediationData(data); // Set the remediation plan for the table
    } catch (err) {
      console.error('Error fetching remediation plan:', err);
      setError('Failed to fetch remediation plan.');
    } finally {
      setLoading(false);
    }
  };

  // Sort the report so that non-compliant policies appear first
  const sortedReport = useMemo(() => {
    if (!report) return null;
    // Flatten all policy checks with service name for easier table rendering and sorting
    const flat = [];
    report.forEach((serviceObj) => {
      serviceObj.policy_checks.forEach((policy) => {
        flat.push({
          ...policy,
          serviceName: serviceObj.serviceName,
        });
      });
    });
    // Sort: non-compliant first, then compliant
    flat.sort((a, b) => {
      if (a.comply === b.comply) return 0;
      return a.comply ? 1 : -1;
    });
    return flat;
  }, [report]);

  const handleReset = () => {
    setAuditsReport([]);
    setMessage('');
    setReport(null);
    setIngestedData([]);
  };


  return (
    <div className="container mx-auto p-4">
      <div className="flex space-x-4 mb-4">
        <button
          onClick={handleIngestEmbeddings}
          className="bg-orange-500 hover:bg-orange-700 text-white font-bold py-2 px-4 rounded"
        >
          Ingest Security Policies
        </button>
        <button
          onClick={handleStartAuditor}
          className="bg-blue-900 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
        >
          Start Security Audit and Generate Report
        </button>
        <button
          onClick={handleReset}
          className="bg-gray-500 hover:bg-gray-700 text-white font-bold py-2 px-4 rounded"
        >
          Reset
        </button>
      </div>
      <div className='mb-4 p-3 rounded'>
        {loading && <p className="mt-4 text-gray-600">Auditor Process in progress...</p>}
        {error && <p className="mt-4 text-red-600">{error}</p>}
      </div>

      {message && (
        <div className={`mb-4 p-3 rounded ${message.includes('successfully') ? 'bg-green-200 text-green-800' : 'bg-red-200 text-red-800'}`}>
          {message}
        </div>
      )}

      {ingestedData.length > 0 && (
        <div className="mb-8">
          <h3 className="text-2xl font-bold text-center text-white bg-gradient-to-r from-indigo-700 to-indigo-900 py-3 rounded shadow-md mb-6">
            Security Policy Information
          </h3>
          <div className="w-full overflow-x-auto rounded-lg shadow-lg">
            <table className="min-w-[1000px] bg-white border border-gray-300 rounded-lg">
              <thead className="bg-indigo-100 sticky top-0 z-10">
                <tr>
                  {["Index Name", "Chunk ID", "Content", "Metadata"].map((header, idx) => (
                    <th key={idx} className="py-3 px-4 border-b text-left text-sm font-semibold text-indigo-900 whitespace-nowrap">
                      {header}
                    </th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {ingestedData.map((item, idx) =>
                  Array.isArray(item.chunks)
                    ? item.chunks.map((chunk, cidx) => (
                      <tr key={`${idx}-${cidx}`} className={cidx % 2 === 0 ? "bg-white" : "bg-gray-50 hover:bg-indigo-50"}>
                        <td className="py-2 px-4 border-b whitespace-nowrap">{item.index_name}</td>
                        <td className="py-2 px-4 border-b whitespace-nowrap">{chunk.chunk_id}</td>
                        <td className="py-2 px-4 border-b">
                          <pre className="whitespace-pre-wrap text-xs bg-gray-100 p-2 rounded text-gray-800">
                            {chunk.content}
                          </pre>
                        </td>
                        <td className="py-2 px-4 border-b">
                          <pre className="whitespace-pre-wrap text-xs bg-gray-100 p-2 rounded text-gray-800">
                            {chunk.metadata}
                          </pre>
                        </td>
                      </tr>
                    ))
                    : null
                )}
              </tbody>
            </table>
          </div>
        </div>
      )}

      {sortedReport && (
        <div className="mb-8">
          <h2 className="text-2xl font-bold text-center text-white bg-gradient-to-r from-blue-700 to-blue-900 py-3 rounded shadow-md mb-6">
            Audit Report
          </h2>
          <div className="w-full overflow-x-auto rounded-lg shadow-lg">
            <table className="min-w-[1100px] bg-white border border-gray-300 rounded-lg">
              <thead className="bg-blue-100 sticky top-0 z-10">
                <tr>
                  {[
                    "Sr. No.",
                    "Service Name",
                    "Security Policy Name",
                    "Compliance Status",
                    "Missing Plugins",
                    "Details"
                  ].map((header, idx) => (
                    <th key={idx} className="py-3 px-4 border-b text-left text-sm font-semibold text-blue-900 whitespace-nowrap">
                      {header}
                    </th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {sortedReport.map((policy, idx) => (
                  <tr key={idx} className={policy.comply ? "bg-green-50 hover:bg-green-100" : "bg-red-50 hover:bg-red-100"}>
                    <td className="py-2 px-4 border-b whitespace-nowrap">{idx + 1}</td>
                    <td className="py-2 px-4 border-b whitespace-nowrap">{policy.serviceName}</td>
                    <td className="py-2 px-4 border-b whitespace-nowrap">{policy.policy_name}</td>
                    <td className="py-2 px-4 border-b font-semibold">
                      {policy.comply ? (
                        <span className="text-green-700">Compliant</span>
                      ) : (
                        <span className="text-red-700">Non-Compliant</span>
                      )}
                    </td>
                    <td className="py-2 px-4 border-b">
                      {!policy.comply && policy.missing_plugins?.length > 0
                        ? policy.missing_plugins.map((mp, mpIdx) => (
                          <div key={mpIdx} className="text-sm text-gray-800">
                            <span className="font-semibold">{mp.plugin || mp.name || JSON.stringify(mp)}</span>
                            {mp.required_for && (
                              <span className="text-xs text-gray-600"> (Required for: {mp.required_for})</span>
                            )}
                          </div>
                        ))
                        : "-"}
                    </td>
                    <td className="py-2 px-4 border-b">
                      {policy.details ? (
                        <pre className="whitespace-pre-wrap text-xs bg-gray-100 p-2 rounded text-gray-800">
                          {policy.details}
                        </pre>
                      ) : (
                        "-"
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          <div className="flex flex-wrap gap-4 mt-6 justify-center">
            <button
              onClick={handleGenerateAuditReport}
              className="bg-blue-600 text-white px-5 py-2 rounded hover:bg-blue-700 transition duration-200 shadow-md"
            >
              Download Audit Report
            </button>
            <button
              onClick={fetchRemediationPlan}
              className="bg-blue-600 text-white px-5 py-2 rounded hover:bg-blue-700 transition duration-200 shadow-md"
            >
              Run Remediation Plan
            </button>

            {loading && <p className="mt-4 text-gray-600 text-sm">Loading remediation plan...</p>}
            {error && <p className="mt-4 text-red-600 text-sm">{error}</p>}
          </div>
        </div>
      )}

      {remediationData.length > 0 && (
        <div className="mb-8">
          <h3 className="text-2xl font-bold text-center text-white bg-gradient-to-r from-blue-700 to-blue-900 py-3 rounded shadow-md mb-6">
            Remediation Plan
          </h3>
          <div className="w-full overflow-x-auto rounded-lg shadow-lg">
            <table className="min-w-[1200px] bg-white border border-gray-300 rounded-lg">
              <thead className="bg-blue-100 sticky top-0 z-10">
                <tr>
                  {[
                    "Service Name",
                    "Policy Name",
                    "Issue",
                    "Missing Plugin(s)",
                    "Recommended Action",
                    "Severity",
                    "Owner",
                    "Estimated Effort",
                    "Impact"
                  ].map((header, idx) => (
                    <th key={idx} className="py-3 px-4 border-b text-left text-sm font-semibold text-blue-900 whitespace-nowrap">
                      {header}
                    </th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {remediationData.map((item, idx) => (
                  <tr key={idx} className={idx % 2 === 0 ? "bg-white" : "bg-gray-50 hover:bg-blue-50"}>
                    <td className="py-2 px-4 border-b whitespace-nowrap">{item.serviceName}</td>
                    <td className="py-2 px-4 border-b whitespace-nowrap">{item.policyName}</td>
                    <td className="py-2 px-4 border-b">
                      <pre className="whitespace-pre-wrap text-xs bg-gray-100 p-2 rounded text-gray-800">
                        {item.issue}
                      </pre>
                    </td>
                    <td className="py-2 px-4 border-b whitespace-nowrap">
                      {Array.isArray(item.missingPlugin)
                        ? item.missingPlugin.join(', ')
                        : item.missingPlugin}
                    </td>
                    <td className="py-2 px-4 border-b">
                      <pre className="whitespace-pre-wrap text-xs bg-gray-100 p-2 rounded text-gray-800">
                        {item.recommendedAction}
                      </pre>
                    </td>
                    <td className="py-2 px-4 border-b font-semibold text-red-600 whitespace-nowrap">{item.severity}</td>
                    <td className="py-2 px-4 border-b whitespace-nowrap">{item.owner}</td>
                    <td className="py-2 px-4 border-b whitespace-nowrap">{item.estimatedEffort}</td>
                    <td className="py-2 px-4 border-b">
                      <pre className="whitespace-pre-wrap text-xs bg-gray-100 p-2 rounded text-gray-800">
                        {item.impact}
                      </pre>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}

      {!report && ingestedData.length === 0 && (
        <div className="mt-8 p-6 bg-gray-100 rounded-lg shadow-md">
          <h2 className="text-2xl font-semibold text-gray-800 mb-4">Welcome to the Autonomous Security Auditor Agent</h2>
          <p className="text-gray-700 mb-4">
            The <b>Autonomous Security Auditor Agentic AI</b> is an advanced application designed to automate the auditing and remediation of API configurations for organizations using Kong API Gateway and Kong AI Gateway. Leveraging AI, large language models (LLMs), and agentic workflows, this tool enables organizations to continuously validate API configurations against enterprise security and compliance policies, identify risks, and remediate issues with minimal manual intervention.
          </p>
          <p className="text-gray-700">
            The Autonomous Security Auditor Agentic AI bridges these gaps by automatically auditing API proxy configurations in Kong against organizational security policies. It generates actionable audit reports and enables one-click remediation, ensuring every API adheres to mandatory security standards. This tool empowers <b>developers, DevSecOps engineers, reviewers, auditors, and architects</b> to maintain compliance and security without deep expertise in every policy, reducing risk and streamlining the review process.
          </p>
        </div>
      )}
    </div>
  );
}

export default Dashboard;
