import React, { useState, useCallback, useRef } from 'react';
import { Button } from './ui/Button';
import { Input } from './ui/Input';
import { Alert, AlertDescription, AlertTitle } from './ui/Alert';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from './ui/Table';
import { Download, AlertCircle, CheckCircle, AlignLeft, AlignJustify } from 'lucide-react';
import { cn } from '../lib/utils';
import { motion } from 'framer-motion';
import '../MigrationStyle.css'
import Header from './Header';
import Footer from './Footer';
import { useNavigate } from 'react-router-dom';
import { stringify } from 'querystring';
import DeployToKong from './DeployToKong'; // Import the popup component
import { useAuth } from './AuthContext';

// environment variables
const AWS_GW_HOSTNAME = process.env.REACT_APP_AWS_GW_HOSTNAME as string;
const AWS_API_KEY = process.env.REACT_APP_AWS_API_KEY as string;
const SERVICE_BASEPATH = process.env.REACT_APP_SERVICE_BASEPATH as string;
const PRESIGNED_PATHSUFFIX = process.env.REACT_APP_PRESIGNED_PATHSUFFIX as string;
const KONG_PROXIES_PATHSUFFIX = process.env.REACT_APP_KONG_PROXIES_PATHSUFFIX as string;
const KONG_PROXIES_RELEASE_PATHSUFFIX = process.env.REACT_APP_KONG_PROXIES_RELEASE_PATHSUFFIX as string;

//
type ButtonKey = 'button1Key' | 'button2Key' | 'button3Key' | 'button4Key' | 'button5Key' | 'button6Key';

//

interface Route {
    routeName: string;
    routePlugins: string[];
    routeMethods: string[];
    routePaths: string[];
}

interface Proxy {
    serviceName: string;
    servicePlugins: string[];
    kongRoutes: Route[];
    outOfScopePlugins: string[];
}

interface MigrationSummary {
    kongSummary: Proxy[];
}


// Main Form Loaded on the homepage
const MigrationForm: React.FC = () => {

    const { isAuthenticated, logout } = useAuth();

    const [migrationSummary, setMigrationSummary] = useState<MigrationSummary | null>(null);
    const [apigeeProxiesLocation, setApigeeProxiesLocation] = useState('');
    const [error, setError] = useState<string | null>(null);

    const [proxyBundleUploadSummary, setProxyBundleUploadSummary] = useState<any[] | null>(null);
    //const [migrationSummary, setMigrationSummary] = useState<any[] | null>(null);
    //const [migrationSummary, setMigrationSummary] = useState<MigrationSummary | null>(null);

    const [preSignedS3URLsSummary, setPreSignedS3URLsSummary] = useState<any[] | null>(null);
    const [downloadUrl, setDownloadUrl] = useState<string | null>(null);
    const [selectedOption, setSelectedOption] = useState('');

    // All buttons ENABLE status, default is false (disabled)
    const [generateS3URLsButtonStatus, setGenerateS3URLsButtonStatus] = useState(false);
    const [externalBundlesUploadButtonStatus, setExternalBundlesUploadButtonStatus] = useState(false);
    const [uploadProxyBundlesButtonStatus, setUploadProxyBundlesButtonStatus] = useState(false);
    const [generateKongProxiesButtonStatus, setGenerateKongProxiesButtonStatus] = useState(false);
    const [resetButtonStatus, setResetButtonStatus] = useState(true);

    //References
    const fileInputRef = useRef<HTMLInputElement>(null);
    const fileTextInputRef = useRef<HTMLTextAreaElement>(null);
    const radioInputRef1 = useRef<HTMLInputElement>(null);
    const radioInputRef2 = useRef<HTMLInputElement>(null);
    const s3URLsButtonRef = useRef<HTMLButtonElement>(null);
    const externalBundlesUploadButtonRef = useRef<HTMLButtonElement>(null);
    const uploadButtonRef = useRef<HTMLButtonElement>(null);
    const kongProxyButtonRef = useRef<HTMLButtonElement>(null);

    // State to store selected files
    const [files, setFiles] = useState<FileList | null>(null);
    const [file, setFile] = useState<File | null>(null);
    const [fileNames, setFileNames] = useState<string | null>(null);

    // Kong proxy deploy popup window
    const [showPopup, setShowPopup] = useState(false);

    const handleDeployKongProxyPopup = () => {
        setShowPopup(true);
    };
    const handleDeployKongProxyPopupClose = () => {
        setShowPopup(false);
    };


    const [loading, setLoading] = useState({
        button1Key: false,
        button2Key: false,
        button3Key: false,
        button4Key: false,
        button5Key: false,
        button6Key: false
    });

    // Handle radio button option change 
    const handleRadioChange = (event: React.ChangeEvent<HTMLInputElement>) => {

        setSelectedOption(event.target.value);
        // setting buttons enable status
        if (event.target.value === 'no-proxy-bundles') {
            setGenerateS3URLsButtonStatus(true);
            setUploadProxyBundlesButtonStatus(false);
            setGenerateKongProxiesButtonStatus(false);
        } else if (event.target.value === 'have-proxy-bundles') {
            setGenerateS3URLsButtonStatus(false);
            setUploadProxyBundlesButtonStatus(true);
            setGenerateKongProxiesButtonStatus(false);
        }

        // set every output to empty when any of radio button is selected
        setError(null);
        setMigrationSummary(null);
        setProxyBundleUploadSummary(null);
        setPreSignedS3URLsSummary(null);

    };

    // Handle file selection operation
    const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setFiles(event.target.files); // Store the selected files
    };

    // Handle file names textarea based operation
    const handleFileNamesChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
        setFileNames(event.target.value); // Store the selected files
    };

    // Handle Generate presigned S3 URLs operation
    const handleGenerateS3URLs = useCallback(async (buttonKey: ButtonKey) => {
        setLoading((prevState) => ({ ...prevState, [buttonKey]: true }));
        //await new Promise(resolve => setTimeout(resolve, 1500));
        setError(null);
        setMigrationSummary(null);
        setProxyBundleUploadSummary(null);
        setPreSignedS3URLsSummary(null);
        setDownloadUrl(null);
        const preSignedS3URLsArray = [];
        try {
            if (fileNames === null || fileNames.trim() === '') {
                //alert("You have to enter correct file names" + fileNames);
                throw new Error("Enter comma (,) separated apigee proxy bundle names!");
                //return;
            }

            const fileNamesList = fileNames.split(",");
            for (let i = 0; i < fileNamesList.length; i++) {
                const fileName = fileNamesList[i];

                // Generate presigned s3 url aws gw api and upload the proxy bundle using same presigned url
                const response = await fetch(AWS_GW_HOSTNAME + SERVICE_BASEPATH + PRESIGNED_PATHSUFFIX + '?fileName=' + fileName + '.zip', {
                    method: 'GET',
                    headers: {
                        'x-api-key': AWS_API_KEY
                    },
                });
                if (!response.ok) {
                    //alert("error occurred!!!");
                    throw new Error('Failed to generate pre-signed S3 URL. Try again later!');
                }
                //alert("success!!"+response.ok);
                const responseFromS3 = await response.json();
                const presignedS3Url = responseFromS3.preSignedS3URL;
                const j = i + 1;
                const s3URLsResponseObject = {
                    srNo: `${j}`,
                    proxyBundleFileName: fileName,
                    preSignedS3URL: presignedS3Url
                };
                preSignedS3URLsArray.push(s3URLsResponseObject);
                //alert('File uploaded successfully');
            }
            setPreSignedS3URLsSummary(preSignedS3URLsArray);
            setGenerateS3URLsButtonStatus(false);
            setExternalBundlesUploadButtonStatus(true);
        } catch (err: any) {
            setError(err.message);
        } finally {
            setLoading((prevState) => ({ ...prevState, [buttonKey]: false }));
        }
        return {
            preSignedS3URLsSummary
        };
    }, [fileNames]);

    // Handle External bundles upload operation
    const handleExternalBundlesUpload = useCallback(async (buttonKey: ButtonKey) => {
        setLoading((prevState) => ({ ...prevState, [buttonKey]: true }));
        //await new Promise(resolve => setTimeout(resolve, 1500));        
        setGenerateKongProxiesButtonStatus(true);
        setExternalBundlesUploadButtonStatus(false);
        setPreSignedS3URLsSummary(null);
        setLoading((prevState) => ({ ...prevState, [buttonKey]: false }));
        return '';
    }, []);

    // Handle proxy bundle upload operation
    const handleUploadProxyBundles = useCallback(async (buttonKey: ButtonKey) => {
        setLoading((prevState) => ({ ...prevState, [buttonKey]: true }));
        setError(null);
        setMigrationSummary(null);
        setProxyBundleUploadSummary(null);
        setDownloadUrl(null);
        try {
            if (!files || files.length === 0) {
                //alert("Please select files to upload.");
                throw new Error("Select Apigee Proxies!");
                //return;
            }

            const response = await callUploadProxyBundlesAPI(files);
            if (response.proxyBundleUploadSummary) {
                setProxyBundleUploadSummary(response.proxyBundleUploadSummary);
                setUploadProxyBundlesButtonStatus(false);
                setGenerateKongProxiesButtonStatus(true);

                // const buttonToDisable = document.getElementById('uploadProxyBundlesButton') as HTMLButtonElement;
                // buttonToDisable.disabled = true;
            } else {
                throw new Error("Internal Error while uploading files to S3 Bucket!");
            }
        } catch (err: any) {
            setError(err.message || 'An unexpected error occurred.');
        } finally {
            setLoading((prevState) => ({ ...prevState, [buttonKey]: false }));
        }
    }, [files]);

    const handleGenerateKongProxies = useCallback(async (buttonKey: ButtonKey) => {
        setLoading((prevState) => ({ ...prevState, [buttonKey]: true }));
        setError(null);
        setProxyBundleUploadSummary(null)
        setDownloadUrl(null);
        try {
            const response = await fetch(AWS_GW_HOSTNAME + SERVICE_BASEPATH + KONG_PROXIES_PATHSUFFIX, {
                method: 'POST',
                headers: {
                    'x-api-key': AWS_API_KEY,
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ "status": "success" }),
            });

            if (!response.ok) {
                const errorBody = await response.json();
                setError(JSON.stringify(errorBody.error));
                return;
                //throw new Error('Failed to Generate Kong Proxies! Try again later!');
            }

            const kongProxiesDownloadUrl = response.headers.get('KongProxy-Download-URL');

            const jsonResponse = await response.json();

            setMigrationSummary(jsonResponse);
            setDownloadUrl(kongProxiesDownloadUrl);

        } catch (err: any) {
            setError(err.message || 'An unexpected error occurred.');
        } finally {
            setLoading((prevState) => ({ ...prevState, [buttonKey]: false }));
        }
    }, []);

    const handleReset = (buttonKey: ButtonKey) => {
        setLoading((prevState) => ({ ...prevState, [buttonKey]: true }));
        setFiles(null);
        setError(null);
        setProxyBundleUploadSummary(null);
        setMigrationSummary(null);
        setPreSignedS3URLsSummary(null);
        setDownloadUrl(null);
        setSelectedOption('');
        setShowPopup(false);

        if (fileInputRef.current) {
            fileInputRef.current.value = ''; // Reset file input
        }
        if (fileTextInputRef.current) {
            fileTextInputRef.current.value = ''; // Reset textarea input
        }
        if (radioInputRef1.current) {
            radioInputRef1.current.checked = false; // Reset radio button
        }
        if (radioInputRef2.current) {
            radioInputRef2.current.checked = false; // Reset radio button
        }
        // disable buttons
        setGenerateS3URLsButtonStatus(false);
        setUploadProxyBundlesButtonStatus(false);
