// API functions definitions (replace with your actual backend API call)
const callUploadProxyBundlesAPI = async (files: FileList): Promise<any> => {
    // Simulate an API call with a delay
    await new Promise(resolve => setTimeout(resolve, 1500));
    const proxyBundleUploadArray = [];
    try {
        for (let i = 0; i < files.length; i++) {
            const file = files[i];
            const response = await fetch(AWS_GW_HOSTNAME + '/api/v1/apigeetokongmaker/s3urls?fileName=' + file.name, {
                method: 'GET',
                headers: {
                    'x-api-key': AWS_API_KEY
                },
                //body: JSON.stringify({ fileName: file.name }),
            });
            if (!response.ok) {
                alert("error occurred!");
                throw new Error('Failed to generate pre-signed S3 URL. Try again later!');
            }
            // alert("success!!"+response.json()+"--"+response.text+"---"+response.text());
            const responseFromS3 = await response.json();
            const presignedS3Url = responseFromS3.preSignedS3URL;
            const fileUploadResponse = await uploadFile(file, presignedS3Url);
            const j = i + 1;
            const fileUploadResponseJsonObject = {
                srNo: `${j}`,
                proxyBundleName: file.name,
                uploadStatus: fileUploadResponse.statusText
            };
            proxyBundleUploadArray.push(fileUploadResponseJsonObject)

            //alert('File uploaded successfully');
        }

    } catch (error) {
        console.error("File upload error:", error);
    } finally {

    }
    return {
        proxyBundleUploadSummary: proxyBundleUploadArray
    };
};

const uploadFile = async (file: File, presignedS3Url: string) => {
    //alert("in uploadFile"+presignedS3Url);
    const response = await fetch(presignedS3Url, {
        method: 'PUT',
        body: file,
        headers: {
            'Content-Type': 'application/zip'
        }
    });
    if (!response.ok) {
        throw new Error('Failed to upload file');
    }
    return response;
};

