import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from './Header';
import Footer from './Footer';
import '../css/CommonStyle.css'
import { useAuth } from './AuthContext';

const HomePage: React.FC = () => {
  const navigate = useNavigate();

  const { isAuthenticated, logout } = useAuth();

  return (
    <div className="flex flex-col h-screen">
      <Header isAuthenticated={isAuthenticated} />
      <div className="flex justify-center items-end h-16 gap-4" style={{ color: 'darkblue' }}>
        <h1 className="text-xl font-bold" >CognizantÂ® API Proxy Migrator for Kong (AKA Apigee to Kong Maker)</h1>
      </div>
      <div className='flex justify-center gap-4' style={{ color: 'darkblue' }}>
        <h3>This automation tool migrates Apigee proxies to their equivalent Kong proxies, including the most commonly used Apigee policies.</h3>
      </div>
      <div className="flex-1 flex justify-center items-center">
        <div className="m-5 p-5 border-2 border-blue-300 rounded-lg bg-blue-100 shadow-lg 
        hover:shadow-xl transition-transform transform hover:scale-105">
          <button
            className="bg-blue-500 text-white py-3 px-6 rounded-lg text-lg 
            hover:bg-blue-700 transition-colors"
            style={{ height: '109px' }}
            onClick={() => navigate('/about-tool')}
          >
            About Tool
            <br />
            &
            <br />
            Benefits
          </button>
        </div>
        <div className="m-5 p-5 border-2 border-blue-300 rounded-lg bg-blue-100 shadow-lg 
        hover:shadow-xl transition-transform transform hover:scale-105" >
          <button
            className="bg-blue-500 text-white py-3 px-6 rounded-lg text-lg 
            hover:bg-blue-700 transition-colors"
            style={{ height: '109px' }}
            onClick={() => navigate('/migration')}
          >
            Let's
            <br />
            Get Started
          </button>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default HomePage;
