import React, { useEffect, useMemo, useState } from 'react';

function Dashboard() {
  const [audits, setAudits] = useState([]);
  const [metadata, setMetadata] = useState([]);
  const [message, setMessage] = useState('');
  const [report, setReport] = useState(null);

  useEffect(() => {
    const fetchAudits = async () => {
      try {
        // Mock API call for audits
        const mockAudits = [
          { id: 1, status: 'Completed', date: '2023-10-26' },
          { id: 2, status: 'Pending', date: '2023-10-25' },
        ];
        setAudits(mockAudits);
      } catch (error) {
        console.error('Error fetching audits:', error);
      }
    };
    fetchAudits();
  }, []);

  const handleIngestEmbeddings = async () => {
    setMessage('');
    setReport(null); // Hide report when ingesting embeddings
    try {
      // Mock API call for embedding
      const response = await fetch('http://localhost:5001/api/v1/knowledge/ingest', { method: 'POST', body: JSON.stringify({}), headers: { 'Content-Type': 'application/json' } });
      if (response.ok) {
        setMessage('All Security Policies Ingested successfully!');
        const data = await response.json();
        if (data.results) {
          const metadataList = data.results.map(item => {
            try {
              // Convert single quotes to double quotes for valid JSON
              const metaStr = item.metadata.replace(/'/g, '"');
              return JSON.parse(metaStr);
            } catch (e) {
              return {};
            }
          });
          setMetadata(metadataList);
        } } else {
        setMessage('Failed to ingest Security Policies.');
      }
    } catch (error) {
      console.error('Error ingesting Security Policies:', error);
      setMessage('Error ingesting Security Policies.');
    }
  };

  const handleStartAuditor = async () => {
    setMessage('');
    setReport(null);
    // The report will be set to data.report if the API call is successful, making it visible.
    try {
      // Mock API call for auditor
      const response = await fetch('http://localhost:5000/api/v1/ai-agents/audit', { method: 'POST', body: JSON.stringify({}), headers: { 'Content-Type': 'application/json' } });
      console.log('Response from auditor API:', response.body);
      if (response.ok) {
        // Mock response data
        const data = {
          report: [
              {
              "serviceName": "MyfirstService",
              "policy_checks": [
                {
                  "policy_name": "Access Control Policy",
                  "comply": true
                },
                {
                  "policy_name": "Audit Logging Policy",
                  "comply": true
                },
                {
                  "policy_name": "API Authentication Policy",
                  "comply": false,
                  "missing_plugins": [
                    {
                      "plugin": "key-auth or jwt or oauth2",
                      "required_for": "API Authentication Policy"
                    }
                  ]
                },
                {
                  "policy_name": "Rate-Limiting Policy",
                  "comply": false,
                  "missing_plugins": [
                    {
                      "plugin": "rate-limiting",
                      "required_for": "Rate-Limiting Policy"
                    }
                  ]
                }
              ]
            },
            {
              "serviceName": "MySecondService",
              "policy_checks": [
                {
                  "policy_name": "Access Control Policy",
                  "comply": true
                },
                {
                  "policy_name": "Audit Logging Policy",
                  "comply": true
                },
                {
                  "policy_name": "API Authentication Policy",
                  "comply": false,
                  "missing_plugins": [
                    {
                      "plugin": "key-auth or jwt or oauth2",
                      "required_for": "API Authentication Policy"
                    }
                  ]
                },
                {
                  "policy_name": "Rate-Limiting Policy",
                  "comply": false,
                  "missing_plugins": [
                    {
                      "plugin": "rate-limiting",
                      "required_for": "Rate-Limiting Policy"
                    }
                  ]
                }
              ]
            }          
          ],
        };
        setReport(data.report);
        setMessage('Auditor started successfully!');
      } else {
        setMessage('Failed to start auditor.');
      }
    } catch (error) {
      console.error('Error starting auditor:', error);
      setMessage('Error starting auditor.');
    }
  };

  const handleGenerateExcel = () => {
    alert('Generating Excel report...');
    // Logic to generate Excel file
  };

  const handleSendEmail = () => {
    alert('Sending report via email...');
    // Logic to send email
  };

  const getStatusRowClass = (status) => {
    switch (status.toLowerCase()) {
      case 'compliant':
        return 'bg-green-100';
      case 'non-compliant':
        return 'bg-red-100';
      default:
        return '';
    }
  };

  const sortedReport = useMemo(() => {
    if (!report) {
      return null;
    }
    // Create a new array to avoid mutating state directly
    return [...report].sort((a, b) => {
      if (a.status === b.status) return 0; // Keep original order for same status
      return a.complianceStatus.toLowerCase() === 'non-compliant' ? -1 : 1;
    });
  }, [report]);

  const handleReset = () => {
    setAudits([]);
    setMessage('');
    setReport(null);
  };


  return (
    <div className="container mx-auto p-4">
      <div className="flex space-x-4 mb-4">
        <button
          onClick={handleIngestEmbeddings}
          className="bg-orange-500 hover:bg-orange-700 text-white font-bold py-2 px-4 rounded"
        >
          Ingest Security Policies
        </button>
        <button
          onClick={handleStartAuditor}
          className="bg-blue-900 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
        >
          Start Security Audit and Generate Report
        </button>
        <button
          onClick={handleReset}
          className="bg-gray-500 hover:bg-gray-700 text-white font-bold py-2 px-4 rounded"
        >
          Reset
        </button>
      </div>

      {message && (
        <div className={`mb-4 p-3 rounded ${message.includes('successfully') ? 'bg-green-200 text-green-800' : 'bg-red-200 text-red-800'}`}>
          {message}
        </div>
      )}

      {metadata.length > 0 && (
        <div className="mb-8">
          <h3 className="text-xl mb-3">Ingested Security Policies Metadata</h3>
          <div className="overflow-x-auto">
            <table className="min-w-full bg-white border border-gray-200">
              <thead>
                <tr>
                  <th className="py-2 px-4 border-b">Policy Name</th>
                  <th className="py-2 px-4 border-b">Description</th>
                  <th className="py-2 px-4 border-b">Version</th>
                  <th className="py-2 px-4 border-b">Ingested At</th>
                </tr>
              </thead>
              <tbody>
                {metadata.map((item, index) => (
                  <tr key={index}>
                    <td className="py-2 px-4 border-b">{item.policyName}</td>
                    <td className="py-2 px-4 border-b">{item.description}</td>
                    <td className="py-2 px-4 border-b">{item.version}</td>
                    <td className="py-2 px-4 border-b">{item.ingestedAt}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}

      {sortedReport && (
        <div className="mb-8">
          <h3 className="text-xl mb-3">Audit Report</h3>
          <div className="overflow-x-auto">
            <table className="min-w-full bg-white border border-gray-200">
              <thead>
                <tr>
                  <th className="py-2 px-4 border-b">Sr. No.</th>
                  <th className="py-2 px-4 border-b">API Service Name</th>
                  <th className="py-2 px-4 border-b">Security Policy</th>
                  <th className="py-2 px-4 border-b">Compliance Status</th>
                  <th className="py-2 px-4 border-b">Missing Plugins</th>
                </tr>
              </thead>
              <tbody>
                {sortedReport.map((service, serviceIdx) =>
                  service.policy_checks.map((policy, policyIdx) => (
                    <tr key={`${serviceIdx}-${policyIdx}`} className={policy.comply ? 'bg-green-100' : 'bg-red-100'}>
                      <td className="py-2 px-4 border-b">{serviceIdx + 1}</td>
                      <td className="py-2 px-4 border-b">{service.serviceName}</td>
                      <td className="py-2 px-4 border-b">{policy.policy_name}</td>
                      <td className="py-2 px-4 border-b">{policy.comply ? 'Compliant' : 'Non-Compliant'}</td>
                      <td className="py-2 px-4 border-b">
                        {!policy.comply && policy.missing_plugins
                          ? policy.missing_plugins.map((mp, mpIdx) => (
                              <div key={mpIdx}>
                                <span className="font-semibold">{mp.plugin}</span> (Required for: {mp.required_for})
                              </div>
                            ))
                          : '-'}
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
          <div className="flex space-x-4 mt-4">
            <button
              onClick={handleGenerateExcel}
              className="bg-blue-900 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
            >
              Generate Excel Report
            </button>
            <button
              onClick={handleSendEmail}
              className="bg-blue-900 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
            >
              Send Report in Email
            </button>
          </div>
        </div>
      )}

      {!report && (
        <div className="mt-8 p-6 bg-gray-100 rounded-lg shadow-md">
          <h2 className="text-2xl font-semibold text-gray-800 mb-4">Welcome to the Autonomous Security Auditor Agent</h2>
          <p className="text-gray-700 mb-4">
            This tool is designed to automate the security auditing process for your services.
            By leveraging advanced AI capabilities, it can identify potential security vulnerabilities
            and compliance issues within your configurations and policies.
          </p>
          <p className="text-gray-700">
            It can be used by Enterprise Security Auditors, compliance teams, and DevSecOps professionals to ensure regulatory compliance and security best practices.
            The agent will analyze your API configurations and provide a detailed report with
            actionable recommendations to enhance your security posture.
          </p>
        </div>
      )}
    </div>
  );
}

export default Dashboard;
