// environment variables
const AWS_GW_HOSTNAME = process.env.REACT_APP_AWS_GW_HOSTNAME as string;
const AWS_API_KEY = process.env.REACT_APP_AWS_API_KEY as string;
const SERVICE_BASEPATH = process.env.REACT_APP_SERVICE_BASEPATH as string;
const LOGIN_PATHSUFFIX = process.env.REACT_APP_LOGIN_PATHSUFFIX as string;


export const login = async (userId: string, password: string) => {
    const response = await fetch(AWS_GW_HOSTNAME + SERVICE_BASEPATH + LOGIN_PATHSUFFIX, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
            'x-api-key': AWS_API_KEY
        },
        body: JSON.stringify({ userId, password }),
    });

    if (!response.ok) {
        return "Invalid credentials";
    }
    const data = await response.json();
    //alert(data.token);
    return data.token;
};
